def extract_complex_verses(text):
    import re
    verses = []
    lines = text.split('\n')

    for line in lines:
        line = line.strip()
        if not line:
            continue

        # Try complex patterns
        for pattern in [
            r'^(\d+\.\d+\.\d+)',
            r'^(\d+\.\d+)',
            r'^(\d+\.)',
            r'^(\d+)',
            r'^([१-९]+\.)',
            r'^([१-९]+:[१-९]+)',
            r'^(॥\s*\d+\s*॥)',
            r'^(॥[१-९]+॥)'
        ]:
            match = re.match(pattern, line)
            if match:
                verses.append({
                    'marker': match.group(1),
                    'content': line,
                    'type': 'verse'
                })
                break
        else:
            # Fallback for irregular patterns
            if len(line) < 20 and re.match(r'^[\d\s\.:-\u0900-\u097F]+$', line):
                verses.append({
                    'marker': line,
                    'content': line,
                    'type': 'irregular'
                })

    return verses
